import { ConfigModule } from '@app/config';
import { HttpModule } from '@nestjs/common';
import { Test, TestingModule } from '@nestjs/testing';
import { PushService } from './push.service';

describe('PushService', () => {
  let service: PushService;

  beforeEach(async () => {
    const module: TestingModule = await Test.createTestingModule({
      imports: [HttpModule, ConfigModule],
      providers: [PushService],
    }).compile();

    service = module.get<PushService>(PushService);
  });

  it('should be defined', () => {
    expect(service).toBeDefined();
  });

  it('should send message', async () => {
    await expect(service.sendMessage(['01000000000'], 'This Is Test Message')).resolves.toBeInstanceOf(Object);
    await expect(service.sendMessage(['01000000000'], 'This is Test MMS message', [{
      body: '',
      name: 'white.jpg',
    }])).resolves.toBeInstanceOf(Object);
    await expect(service.sendMessage(['01000000000'], 'This is Test LMS Message.Lorem ipsum dolor sit amet, consectetur adipiscing elit. Suspendisse augue velit, congue et congue et, tempus id nunc. Sed varius cras amet.')).resolves.toBeInstanceOf(Object);
  });
});
